    private void agregarDatoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_agregarDatoActionPerformed
        // TODO add your handling code here:
        numero = Integer.parseInt(numeroIngresado.getText());
        posicion = Integer.parseInt(posicionIngresada.getText());
        arreglo[posicion] = numero;
    }//GEN-LAST:event_agregarDatoActionPerformed

