    private void borrarDatoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_borrarDatoActionPerformed
        // TODO add your handling code here:
        numero = Integer.parseInt(numeroIngresado.getText());
        posicion = Integer.parseInt(posicionIngresada.getText());
        arreglo[posicion] = 0;
    }//GEN-LAST:event_borrarDatoActionPerformed

    private void limpiarArregloActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_limpiarArregloActionPerformed
        // TODO add your handling code here:
        for (int i = 0; i < arreglo.length; i++) {
            arreglo[i] = 0;
        }

    }//GEN-LAST:event_limpiarArregloActionPerformed

